query {
    organizations(
        where:{
           status:{eq:READY}
           onboardingCompleted:{eq:true}
        }
        order:{creationDate:DESC}
    ) {
        edges {
            node {
                socialName
                tenantId
                id
                legislationCode
                creationDate
            }
       }
        totalCount
        pageInfo {
            hasNextPage
        }
    }     
}

query ($name: String!) {
    organizations (
        first:1
        where: {
            socialName: { startsWith: $name }
            status: { eq:READY}
            onboardingCompleted: { eq: true }
        }
        order: { creationDate: DESC }
    ) {
        edges {
            node {
                socialName
                tenantId
                id
                legislationCode
                creationDate
            }
        }
    }
}

