           {accountingEntry:{date: {gte: "2021-01-01", lte: "2023-01-31"}}}
        ]
      }
       order: [{ accountingEntry:{date: ASC }}]
    )

     {
        edges {
            node {
                accountingEntry{
                    date
                    number
                    journalType{
                        code
                        name
                    }
                }
                accountingEntryPayment{
                    matching
                }

                description
                debitAmount
                creditAmount
            }
        }
        totalCount
        pageInfo {
            hasNextPage
        }
    }     
}

