query {
    accountingEntryLines(
        where: {
          and: [
            {accountingEntry: {date: {gte: "2024-01-01", lte: "2024-12-31"}}},
            {accountingEntry: {journalType: {type: {eq: SALES_INVOICE}}}},
            {accountingEntryThirdParty: {code: {neq: null}}}
            {accountingEntryInvoice: {openItems: {some: {status: {in: [NOT_SPECIFIED, PARTIAL, ]}}}}}
          ]
        }
        order: [
            {accountingEntry: {journalType: {code: ASC}}}
            {accountingEntry: {date: ASC}},
        ]
    ) {
        edges {
            node {
                accountingEntryThirdParty{
                    code
                    socialName
                }
                accountingEntryInvoice{
                    externalInvoiceNumber
                    openItems{
                        id
                        status
                        amount
                        dueDate
                        paidAmountAccumulated
                        paymentMean{
                            description
                        }
                    }
                }
            }
        }
        totalCount
        pageInfo {
            hasNextPage
        }
    }     
}

query GetAccountingEntryLines($thirdPartyCode: String!, $invoiceNumber: String!) {
    accountingEntryLines(
        where: {
          and: [
            {accountingEntry: {journalType: {type: {eq: SALES_INVOICE}}}},
            {accountingEntryThirdParty: {code: {eq: $thirdPartyCode}}},
            {accountingEntryInvoice: {externalInvoiceNumber: {eq: $invoiceNumber}}},
            {accountingEntryInvoice: {openItems: {some: {status: {in: [NOT_SPECIFIED, PARTIAL]}}}}}
          ]
        }
        order: [
            {accountingEntry: {journalType: {code: ASC}}},
            {accountingEntry: {date: ASC}},
        ]
    ) {
        edges {
            node {
                accountingEntryThirdParty{
                    code
                    socialName
                }
                accountingEntryInvoice{
                    externalInvoiceNumber
                    openItems{
                        id
                        status
                        amount
                        dueDate
                        paidAmountAccumulated
                        paymentMean{
                            description
                        }
                    }
                }
            }
        }
        totalCount
        pageInfo {
            hasNextPage
        }
    }     
}

